<style type="text/css"></style><script type="text/javascript">$(function() {reset();init();slideshowKeypress();slideshowClick();});var filterQuery = '<?= $filter; ?>';function changeFilter(f) {filterQuery = f;}function deleteslideshow() {var slideshowId = $('.delete-slideshow-button').attr('data-slideshow-id');var slideshowUpdated = $('.delete-slideshow-button').attr('data-slideshow-updated');$('.ui.basic.modal.modal-warning-delete').modal('hide');$('.ui.text.loader').html('Connecting to Database...');$('.ui.dimmer.all-loader').dimmer('show');$.ajax({data :{updated: slideshowUpdated,"<?= $csrf['name'] ?>": "<?= $csrf['hash'] ?>"},dataType: 'JSON',error: function() {$('.ui.dimmer.all-loader').dimmer('hide');$('.ui.basic.modal.all-error').modal('show');$('.all-error-text').html('Server Error.');},success: function(data){if (data.status == 'success') {$('.ui.text.loader').html('Redirecting...');window.location.reload();}else {$('.ui.dimmer.all-loader').dimmer('hide');$('.ui.basic.modal.all-error').modal('show');$('.all-error-text').html(data.message);}},type : 'POST',url : '<?= base_url() ?>slideshow/ajax_delete/'+ slideshowId +'/',xhr: function() {var percentage = 0;var xhr = new window.XMLHttpRequest();xhr.upload.addEventListener('progress', function(evt) {$('.ui.text.loader').html('Validating Data..');}, false);xhr.addEventListener('progress', function(evt) {$('.ui.text.loader').html('Delete Data from Database...');}, false);return xhr;},});}function filter(page) {var searchQuery = ($('.input-search').val() == '') ? '' : $.base64('encode', $('.input-search').val());window.location.href = '<?= base_url(); ?>slideshow/view/'+ page +'/'+ filterQuery +'/'+ searchQuery +'/';}function init() {$('.dropdown-search, .dropdown-filter').dropdown({allowAdditions: true});}function reset() {$('.input-search').val("<?= $query; ?>");$('#input-page').val("<?= $page; ?>");}function slideshowClick() {$('.button-prev').click(function() {var page = parseInt('<?= $page; ?>');page = page - 1 ;if (page <= 0) {return;}filter(page);});$('.button-next').click(function() {var page = parseInt('<?= $page; ?>');var maxPage = parseInt('<?= $count_page; ?>');page = page + 1 ;if (page > maxPage) {return;}filter(page);});$('.open-modal-warning-delete').click(function() {var slideshowId = $(this).attr('data-slideshow-id');var slideshowName = $(this).attr('data-slideshow-name');var slideshowUpdated = $(this).attr('data-slideshow-updated');$('.delete-slideshow-title').html('Delete slideshow ' + slideshowName);$('.delete-slideshow-button').attr('data-slideshow-id', slideshowId);$('.delete-slideshow-button').attr('data-slideshow-updated', slideshowUpdated);$('.ui.basic.modal.modal-warning-delete').modal('show');});}function slideshowKeypress() {$('.input-search').keypress(function(e) {if (e.which == 13) {var page = 1;filter(page);}});$('#input-page').keypress(function(e) {if (e.which == 13) {var page = $('#input-page').val();filter(page);}});}</script><!-- Dashboard Here --><div class="ui basic modal modal-warning-delete"><div class="ui icon header"><i class="trash outline icon delete-icon"></i><span class="delete-slideshow-title">Delete Slideshow</span></div><div class="content text-center"><p>You're about to delete this Slideshow. You will not be able to undo this action. Are you sure?</p></div><div class="actions"><div class="ui red basic cancel inverted button"><i class="remove icon"></i>No</div><div class="ui green ok inverted button delete-slideshow-button" onclick="deleteslideshow();"><i class="checkmark icon"></i>Yes</div></div></div><div class="main-content"><div class="ui top attached menu table-menu"><div class="item item-add-button">Slideshow Lists</div><div class="right menu"><? if (isset($acl['website']) && $acl['website']->add != ''): ?><a class="item item-add-button" href="<?= base_url(); ?>slideshow/add/"><i class="add circle icon"></i> Add Slideshow</a><? endif; ?><div class="item"><div class="ui dropdown dropdown-filter"><div class="text"><?= ucwords($filter); ?></div><i class="dropdown icon"></i><div class="menu"><div class="item" onclick="changeFilter('all');">All</div></div></div></div><div class="ui right aligned category search item search-item-container"><div class="ui transparent icon input"><input class="input-search" placeholder="Search..." type="text"><i class="search link icon"></i></div><div class="results"></div></div></div></div><div class="ui bottom attached segment table-segment"><table class="ui striped selectable sortable celled table"><thead><tr><th class="td-icon">Action</th><th>Name</th></tr></thead><tbody><? if (count($arr_slideshow) <= 0): ?><tr><td colspan="4">No Result Founds</td></tr><? else: ?><? foreach ($arr_slideshow as $slideshow): ?><tr><td class="td-icon"><? if (isset($acl['website']) && $acl['website']->edit > 0): ?><a href="<?= base_url(); ?>slideshow/edit/<?= $slideshow->id; ?>/"><span class="table-icon" data-content="Edit slideshow"><i class="edit icon"></i></span></a><? endif; ?><? if (isset($acl['website']) && $acl['website']->delete > 0): ?><a class="open-modal-warning-delete" data-slideshow-id="<?= $slideshow->id; ?>" data-slideshow-name="<?= $slideshow->name; ?>" data-slideshow-updated="<?= $slideshow->updated; ?>"><span class="table-icon" data-content="Delete slideshow"><i class="trash outline icon"></i></span></a><? endif; ?></td><td><?= $slideshow->name; ?></td></tr><? endforeach; ?><? endif; ?></tbody><tfoot><tr><th colspan="4"><button class="ui button button-prev">Prev</button><span><div class="ui input input-page"><input id="input-page" placeholder="" type="text"></div> / <?= $count_page; ?></span><button class="ui button button-next">Next</button></th></tr></tfoot></table></div></div></body></html>